import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './CampaignDetail.css';

function CampaignDetail() {
  const { id } = useParams();
  const [campaign, setCampaign] = useState(null);

  useEffect(() => {
    const fetchCampaign = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/campaigns/${id}`);
        setCampaign(response.data);
      } catch (error) {
        console.error('Error fetching campaign:', error);
      }
    };
    fetchCampaign();
  }, [id]);

  if (!campaign) return <div>Loading...</div>;

  const progress = (campaign.current_amount / campaign.goal_amount) * 100;

  return (
    <div className="campaign-detail">
      <div className="campaign-header">
        <img 
          src={campaign.image_url || '/default-campaign-image.jpg'} 
          alt={campaign.title} 
          className="campaign-image"
        />
        <h1>{campaign.title}</h1>
      </div>

      <div className="campaign-content">
        <div className="campaign-main">
          <div className="campaign-story">
            <h2>Campaign Story</h2>
            <p>{campaign.description}</p>
          </div>
        </div>

        <div className="campaign-sidebar">
          <div className="fundraising-stats">
            <div className="progress-container">
              <div className="progress-bar">
                <div 
                  className="progress" 
                  style={{width: `${progress}%`}}
                ></div>
              </div>
              <div className="progress-stats">
                <span className="amount-raised">${campaign.current_amount}</span>
                <span className="goal-amount">raised of ${campaign.goal_amount}</span>
              </div>
            </div>

            <button className="donate-button">
              Donate Now
            </button>

            <div className="share-buttons">
              <button>Share</button>
              <button>Follow</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CampaignDetail;
